crypt_hash.hash:
let passwordHash       = arguments [ 2 ].HmacSHA256 ( arguments [ 0 ], arguments [ 1 ] )
let passwordHashBase64 = arguments [ 2 ].enc.Base64.stringify ( passwordHash )

return passwordHashBase64

crypt_hash.encrypt:
let secretKey 		= arguments [ 1 ]	
let encryptionKey 	= secretKey.substr ( 0, 16 )
let iv_ 			= secretKey.substr ( secretKey.length - 16 )
let key				= arguments [ 2 ].enc.Utf8.parse ( encryptionKey )
let key_iv		    = arguments [ 2 ].enc.Utf8.parse ( iv_ )
let plaintext		= arguments [ 2 ].enc.Utf8.parse ( JSON.stringify ( arguments [ 0 ] ) )
let ciphertext      = arguments [ 2 ].AES.encrypt(
	plaintext,
	key,
	{
		iv     : key_iv,
		mode   : arguments [ 2 ].mode.CBC,
		padding: arguments [ 2 ].pad.Pkcs7
	}
).ciphertext.toString ( arguments [ 2 ].enc.Base64 )

return ciphertext

crypt_hash.decrypt:
let secretKey       = arguments [ 1 ]
let encryptionKey 	= secretKey.substr ( 0, 16 )
let iv_ 			= secretKey.substr ( secretKey.length - 16 )
let key				= arguments [ 2 ].enc.Utf8.parse ( encryptionKey )
let key_iv		    = arguments [ 2 ].enc.Utf8.parse ( iv_ )

let bytes = arguments [ 2 ].AES.decrypt ( 
	arguments [ 0 ].toString ( arguments [ 2 ].enc.Utf8 ), 
	key, 
	{
		iv     : key_iv,
		mode   : arguments [ 2 ].mode.CBC,
		padding: arguments [ 2 ].pad.Pkcs7
	}
)

let originalText = arguments [ 2 ].enc.Utf8.stringify ( bytes ).toString( 'utf-8' ) 

return JSON.parse ( originalText )

